mapper.select:
-- 查询用户拥有的所有权限编码 SELECT DISTINCT p.code FROM t_user_role ur JOIN t_role_permission rp ON ur.role_id = rp.role_id JOIN t_permission p ON rp.permission_id = p.id WHERE ur.user_id = #{userId}
UNION
-- 查询用户拥有的所有角色编码 SELECT DISTINCT r.code FROM t_user_role ur JOIN t_role r ON ur.role_id = r.id WHERE ur.user_id = #{userId}
